use bellperson_gadgets::group::RsaQuotientGroup;
use bellperson_gadgets::hash::circuit::CircuitHasher;
use bellperson_gadgets::hash::hashes::Poseidon;
use bellperson_gadgets::hash::Hasher;
use bellperson_gadgets::set::int_set::exp::parallel::ParExpComb;
use bellperson_gadgets::set::int_set::ExpSet;
use bellperson_gadgets::set::rsa::SetBenchInputs;
use bellperson_gadgets::util::verbose;
use docopt::Docopt;
use pairing::bls12_381::Bls12;
use pairing::Engine;
use serde::Deserialize;

const USAGE: &str = "
RSA Set Initialization

Usage:
  rsa_set_init <transactions> <capacity>
  rsa_set_init (-h | --help)

Options:
  -h --help      Show this screen.
";

// From https://en.wikipedia.org/wiki/RSA_numbers#RSA-2048
const RSA_2048: &str = "";
const RSA_SIZE: usize = 2048;
const ELEMENT_SIZE: usize = 5;

#[derive(Debug, Deserialize)]
struct Args {
    arg_transactions: usize,
    arg_capacity: usize,
}

fn main() {
    color_backtrace::install();
    let args: Args = Docopt::new(USAGE)
        .and_then(|d| d.deserialize())
        .unwrap_or_else(|e| e.exit());
    verbose::set_verbose_mode(true);
    rsa_bench::<Bls12, _>(&args, Poseidon::default());
}

fn rsa_bench<E: Engine, H: Hasher<F = E::Fr> + CircuitHasher<E = E>>(args: &Args, hash: H) {
    println!("Initializing accumulators, circuits");
    let n_untouched = (1usize << args.arg_capacity).saturating_sub(args.arg_transactions);

    println!("Constructing initial and final states");
    let _inputs = SetBenchInputs::<_, ExpSet<RsaQuotientGroup, ParExpComb>>::from_counts(
        n_untouched,
        args.arg_transactions,
        args.arg_transactions,
        ELEMENT_SIZE,
        hash.clone(),
        RSA_SIZE,
        32,
        RsaQuotientGroup::from_strs("2", RSA_2048),
    );
    println!("Done");
}
